import { describe, PropTypes } from 'react-desc';

export default (function (Element) {
  var DocumentedElement = describe(Element).description('A small image stamp used for avatars or image thumbnails.\n      ').usage('\n      $ npm install grommet-controls\n \n      import { ImageStamp } from \'grommet-controls\';\n\n      <ImageStamp src=\'...\' />\n    ');

  DocumentedElement.propTypes = {
    round: PropTypes.oneOf(['xsmall', 'small', 'medium', 'large', 'full']).description('How much to round the corners of the stamp.'),
    size: PropTypes.oneOf(['small', 'medium', 'large', 'xlarge', 'xxlarge']).description('The size of the image stamp.').defaultValue('large')
  };

  return DocumentedElement;
});