import { describe, PropTypes } from 'react-desc';

export default (function (Select) {
  var DocumentedSelect = describe(Select).description('Grommet 2 Select with custom label editor.').usage('\n    $ npm install grommet-controls\n\n    import { Select } from \'grommet-controls\';\n\n    <Select />\n    ');

  DocumentedSelect.propTypes = {
    label: PropTypes.func.description('A function to render/edit the selected items. `({ placeholder, value, onChange }) => {}`')
  };

  return DocumentedSelect;
});